import React, {useState} from "react";
// import logo from "./logo.svg";
import "./App.css";
import InputBox from "./components/inputbox/inputBox";
import ReportForm from "./components/reportForm";

/*global chrome*/
function App() {
  var PRICE = "price";
  const [taxRate, setTaxRate] = useState(1.08);
  var whitelist = true;
  chrome.tabs.query({active: true, currentWindow: true}, function(tabs) {
    chrome.tabs.sendMessage(tabs[0].id, {type: PRICE, tax: taxRate, whitelist: whitelist}, function(response) {
        console.log("save response", response.save);
    }); 
  });   
  return (
    <div>
      <InputBox tax = {(tax)=>setTaxRate(tax)}/>
      <ReportForm />
    </div>
  );
}

export default App;
