    // }
    // chrome.tabs.query({active: true}, function(tabs) {
    // var tab = tabs[0];
    // tab_title = tab.title;
    // chrome.tabs.executeScript(tab.id, {
    //     code: 'document.querySelector("h1").textContent'
    // }, display_h1);
    // });

    return(
    <h1 ref = {title} >''</h1>
    )
}
