        if (node.nodeValue.includes('$')) {
            list.append(node);
            title.current.innerHTML = title + node.nodeValue;
            console.log(list.length);
        }
        node.childNodes.forEach(element => {
            if (node.getAttribute('visited') === null){
                findprice(element);
            }
        });  
    }

